import { Modal } from "bootstrap";

function DeleteModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          <ImBaffled></ImBaffled> Are you sure?
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* <h4>{props.tasks[props.num].title}</h4> */}
        <p>정말루 삭제하실건가용?</p>
      </Modal.Body>
      <Modal.Footer>
        <button onClick={props.onHide}>Close</button>
        {/* setmodalshow(false)를 onHide function으로 받음 */}
        <button
          onClick={() => {
            //state 변경함수의 처리속도가 조금 느려서 바로바로 안바뀔 수 있다.(비동기로 방식해서)
            let copy = [...props.tasks];
            console.log(props.num);
            copy.splice(props.num, 1);
            props.setTasks(copy);
            props.deleteTask(props.tasks[props.num]._id);
            props.onHide();
          }}
        >
          Delete
        </button>
      </Modal.Footer>
    </Modal>
  );
}

export default DeleteModal;
