import React from "react";
import Modal from "react-modal";

function EditModal({ taskId, title, content, onClickCancle, onClickConfirm }) {
  const [inputValue, setInputValue] = useState("");

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  return (
    <div class="form-group">
      <label>title</label>
      <input type="text" className="form-control" name="title" value={title} />
      <label>content</label>
      <input
        type="text"
        className="form-control"
        name="content"
        value={content}
      />

      <button onClick={onClickCancle}>Close Modal</button>
      <button onClick={onClickConfirm}>Update Task</button>
    </div>
  );
}

export default EditModal;
