import React from "react";
import Modal from "react-modal";

function EditModal({ taskId, title, content, onClickCancle, onClickConfirm }) {
  return (
    <div class="form-group">
      <label>title</label>
      <input type="text" className="form-control" name="title" value={title} />
      <label>content</label>
      <input
        type="text"
        className="form-control"
        name="content"
        value={content}
      />
      <input
        type="text"
        value={timer.timer}
        style={{ display: "none" }}
        name="created_at"
      />

      <button type="submit" className="btn btn-outline-secondary">
        Submit
      </button>

      <button onClick={onClickCancle}>Close Modal</button>
      <button onClick={onClickConfirm}>Delete Task</button>
    </div>
  );
}

export default EditModal;
