import React from "react";
import Modal from "react-modal";

function DeleteModal(props) {
  const handleDelete = () => {
    let copy = [...props.tasks];
    copy.splice(props.num, 1);
    props.setTasks(copy);
    props.deleteTask(props.tasks[props.num]._id);
    props.setDeleteModal(false);
  };

  return (
    <div>
      <h2>Hello, this is a modal!</h2>
      <button onClick={props.setDeleteModal(false)}>Close Modal</button>
      <button onClick={props.deleteTask}>Delete Task</button>
    </div>
  );
}

export default DeleteModal;
