import React from "react";
import Modal from "react-modal";

function EditModal({ taskId, title, content, onClickCancle, onClickConfirm }) {
  return (
    <div className="container mt-3">
      <form action="http://localhost:8080/api/edit" method="POST">
        <div class="form-group">
          <label>title</label>
          <input
            type="text"
            className="form-control"
            name="title"
            value={title}
          />
          <label>content</label>
          <input
            type="text"
            className="form-control"
            name="content"
            value={content}
          />
          <input
            type="text"
            value={timer.timer}
            style={{ display: "none" }}
            name="created_at"
          />
        </div>

        <button type="submit" className="btn btn-outline-secondary">
          Submit
        </button>
      </form>{" "}
      <button onClick={onClickCancle}>Close Modal</button>
      <button onClick={onClickConfirm}>Delete Task</button>
    </div>
  );
}

export default EditModal;
