import React, { useState } from "react";
import Modal from "react-modal";

function EditModal({ taskId, title, content, onClickCancle, onClickConfirm }) {
  const [inputTitle, setInputTitle] = useState(title);
  const [inputContent, setInputContent] = useState(content);

  const handleTitle = (e) => {
    setInputTitle(e.target.value);
  };
  const handleContent = (e) => {
    setInputContent(e.target.value);
  };

  return (
    <div>
      <form action="http://localhost:8080/api/edit" method="POST">
        <input
          type="text"
          value={taskId}
          style={{ display: "none" }}
          name="taskId"
        />
        <label>title</label>
        <input
          type="text"
          className="form-control"
          name="title"
          value={inputTitle}
          onChange={handleTitle}
        />
        <label>content</label>
        <input
          type="text"
          className="form-control"
          name="content"
          value={inputContent}
          onChange={handleContent}
        />

        <button onClick={onClickCancle}>Close Modal</button>
        <button type="submit" onClick={onClickConfirm}>
          Update Task
        </button>
      </form>
    </div>
  );
}

export default EditModal;
